import React from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Person from "./pages/Person";
import HomePage from "./pages/Home";
import NotFound from "./pages/NotFound";
import StarfieldAnimation from "react-starfield-animation";
import Sound from "react-sound";
import Theme from "./sounds/Theme.mp3";
import "./App.css";

function App() {
  const [playSound, setPlaySound] = React.useState(false); // to toggle the theme music on the landing page

  return (
    <>
      {/* Sound button */}
      <button
        style={{
          color: "#ffeb00",
          position: "absolute",
          top: "5%",
          right: "5%",
          zIndex: "500",
          padding: "10px",
          border: 0,
          fontWeight: "bold",
          fontSize: "15px",
        }}
        onClick={() => setPlaySound(!playSound)}
      >
        {playSound ? (
          <img
            src="data:image/svg+xml;base64,"
            alt="sound-on"
            width="25px"
          />
        ) : (
          <img
            src="data:image/svg+xml;base64,"
            alt="sound-off"
            width="25px"
          />
        )}
      </button>

      {/* Sound component */}
      <Sound
        url={Theme}
        playStatus={playSound ? Sound.status.PLAYING : Sound.status.STOPPED}
        playFromPosition={0 /* in milliseconds */}
        loop={true}
      />

      {/* background star animation component */}
      <StarfieldAnimation
        style={{
          position: "absolute",
          width: "100%",
          height: "100%",
          zIndex: 0,
        }}
      />

      {/* Main app components */}
      <div className="app">
        <Router>
          <Switch>
            <Route path="/" exact>
              <HomePage />
            </Route>

            <Route path="/person/:id">
              <Person />
            </Route>

            <Route>
              <NotFound />
            </Route>
          </Switch>
        </Router>
      </div>
    </>
  );
}

export default App;
