import React, { useContext } from "react";
import logo from "./star-wars-logo.png";
import { useHistory } from "react-router-dom";
import useDebounce from "../../Hooks/useDebounce";
import { CharacterContext } from "../../Contexts/Fetch/CharacterContextprovider";
import "./index.css";
import { v4 as uuidv4 } from "uuid";
import styled from "styled-components";

const Results = styled.div`
  max-height: 300px;
  overflow-y: auto;
  border-radius: 20px;
  margin-bottom: 10px;

  & :nth-child(${({ active }) => active}) {
    color: #ffeb00;
  }
`;

const Character = styled.div`
  & :hover {
    color: #ffeb00;
  }
`;
function HomePage() {
  let history = useHistory();
  const [query, setQuery] = React.useState("");

  const [spinner, setSpinner] = React.useState(false);
  const [active, setActive] = React.useState(0);
  const scrollRef = React.useRef();
  const { data, handleSearch, setData, setCurrentCharacter, send, changeSend } =
    useContext(CharacterContext);
  const debouncedSearchTerm = useDebounce(query, 500);

  React.useEffect(() => {
    if (debouncedSearchTerm.trim() !== "") {
      handleSearch(debouncedSearchTerm);
      setSpinner(false);
      setActive(0);
    } else {
      setData("");
      setSpinner(false);
      changeSend(false);
      setActive(0);
    }
    // eslint-disable-next-line
  }, [debouncedSearchTerm]);

  function setCharacter(character) {
    setCurrentCharacter(character);
    history.push(`/person/${character.name.trim().split(" ").join("-")}`);
  }

  function handleKeyChange(e) {
    switch (e.keyCode) {
      case 38: {
        //arrow key up
        if (active === 2) {
          setActive(0);
          scrollRef.current && (scrollRef.current.scrollTop = 0);
        } else if (active <= 0) {
          setActive(data.length + 1);
          scrollRef.current &&
            (scrollRef.current.scrollTop =
              scrollRef.current.scrollTop + data.length * 50);
        } else {
          setActive((prev) => prev - 1);
          scrollRef.current && (scrollRef.current.scrollTop -= 50);
        }
        break;
      }
      case 40: {
        //arrow key down

        if (active === 0) {
          setActive((prev) => prev + 2);
        } else if (active > data.length) {
          setActive(0);
          scrollRef.current && (scrollRef.current.scrollTop = 0);
        } else {
          setActive((prev) => prev + 1);
          scrollRef.current &&
            active > 4 &&
            (scrollRef.current.scrollTop += 80);
        }
        break;
      }
      case 13: {
        //enter
        if (active && data.length > 0) {
          setCurrentCharacter(data[active - 2]);
          history.push(
            `/person/${data[active - 2].name.trim().split(" ").join("-")}`
          );
        }
        break;
      }
      default: {
        return;
      }
    }
  }

  return (
    <div className="search" onKeyUp={(e) => handleKeyChange(e)}>
      <div className="logo">
        <img src={logo} alt="Star Wars Logo" />
      </div>

      <div className="search__searchbox">
        <div className="search__searchbox__input">
          <input
            placeholder="Search characters by name"
            value={query}
            onChange={(e) => {
              setQuery(e.target.value);
              setSpinner(true);
            }}
          />
          {!spinner && (
            <div>
              {data.length > 0 ? (
                <div
                  style={{
                    display: "flex",
                  }}
                >
                  <img
                    src="data:image/svg+xml;base64,"
                    alt="close"
                    width="15px"
                    style={{
                      padding: "10px",
                      borderRadius: "100%",
                    }}
                    onClick={() => {
                      setData("");
                      setQuery("");
                      changeSend(false);
                    }}
                  />

                  <hr />
                  <img
                    src="data:image/svg+xml;base64,"
                    alt="center"
                    width="15px"
                    style={{
                      backgroundColor: "#FFEB00",
                      padding: "10px",
                      marginLeft: "10px",
                      borderRadius: "100%",
                    }}
                  />
                </div>
              ) : (
                <img
                  src="data:image/svg+xml;base64,"
                  alt="center"
                  width="15px"
                  style={{
                    backgroundColor: "#FFEB00",
                    padding: "10px",
                    borderRadius: "100%",
                  }}
                />
              )}
            </div>
          )}
          {spinner && <div className="loader"></div>}
        </div>

        {data.length > 0 ? (
          <>
            <hr></hr>
            <Results className="results" active={active} ref={scrollRef}>
              <div></div>
              {data &&
                data?.map((character, index) => {
                  return (
                    <Character
                      className="search__searchbox__results"
                      key={uuidv4()}
                      onMouseOver={() => {
                        setActive(index + 2);
                      }}
                      onClick={() => setCharacter(character, index)}
                    >
                      <div className="character__info">
                        <div>
                          <p style={{ fontSize: "15px" }}>{character.name}</p>
                          <p style={{ fontSize: "12px", color: "#babcbe" }}>
                            {character.birth_year}{" "}
                          </p>
                        </div>
                        <p style={{ fontSize: "12px", color: "#babcbe" }}>
                          {character.gender}
                        </p>
                      </div>
                    </Character>
                  );
                })}
            </Results>
          </>
        ) : null}
      </div>

      {send && data.length === 0 && (
        <p style={{ fontSize: "12px", color: "#babcbe" }}>
          No results found. Try again...
        </p>
      )}
    </div>
  );
}

export default HomePage;
